import React, { useState } from 'react';
import { Calendar, Download, Moon, Sun } from 'lucide-react';
import { Analytics } from '@vercel/analytics/react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

export default function ForwardVolCalculator() {
  const [date1, setDate1] = useState('2025-10-24');
  const [date2, setDate2] = useState('2025-10-31');
  const [iv1, setIv1] = useState('50');
  const [iv2, setIv2] = useState('40');
  const [spotPrice, setSpotPrice] = useState('100');
  const [strikePrice, setStrikePrice] = useState('100');
  const [riskFreeRate, setRiskFreeRate] = useState('4');
  const [dividend, setDividend] = useState('0');
  const [showCalendar1, setShowCalendar1] = useState(false);
  const [showCalendar2, setShowCalendar2] = useState(false);
  const [result, setResult] = useState(null);
  const [darkMode, setDarkMode] = useState(false);
  const [tradeHistory, setTradeHistory] = useState([]);
  const [callPriceSlider, setCallPriceSlider] = useState(null);
  const [putPriceSlider, setPutPriceSlider] = useState(null);
  const [adjustedFFCall, setAdjustedFFCall] = useState(null);
  const [adjustedFFPut, setAdjustedFFPut] = useState(null);
  const [pricingModel, setPricingModel] = useState('blackscholes');
  const [ticker, setTicker] = useState('');
  const [loading, setLoading] = useState(false);
  const [loadError, setLoadError] = useState(null);
  const [apiKey, setApiKey] = useState(localStorage.getItem('polygonKey') || '');
  const [showTickerDropdown, setShowTickerDropdown] = useState(false);
  const [filteredTickers, setFilteredTickers] = useState([]);
  const [volumeCallT1, setVolumeCallT1] = useState(null);
  const [volumeCallT2, setVolumeCallT2] = useState(null);
  const [volumePutT1, setVolumePutT1] = useState(null);
  const [volumePutT2, setVolumePutT2] = useState(null);
  const [oiCallT1, setOiCallT1] = useState(null);
  const [oiCallT2, setOiCallT2] = useState(null);
  const [oiPutT1, setOiPutT1] = useState(null);
  const [oiPutT2, setOiPutT2] = useState(null);
  const [showScanner, setShowScanner] = useState(false);
  const [scanResults, setScanResults] = useState([]);
  const [scanning, setScanning] = useState(false);
  const [minFF, setMinFF] = useState(30);
  const [scanProgress, setScanProgress] = useState({ current: 0, total: 0 });
  const [scanLogs, setScanLogs] = useState([]);
  const [dataSource, setDataSource] = useState(null);

  // Popular US stocks for autocomplete
  const popularStocks = [
    { symbol: 'AAPL', name: 'Apple Inc.' },
    { symbol: 'MSFT', name: 'Microsoft Corporation' },
    { symbol: 'GOOGL', name: 'Alphabet Inc.' },
    { symbol: 'AMZN', name: 'Amazon.com Inc.' },
    { symbol: 'NVDA', name: 'NVIDIA Corporation' },
    { symbol: 'META', name: 'Meta Platforms Inc.' },
    { symbol: 'TSLA', name: 'Tesla Inc.' },
    { symbol: 'BRK.B', name: 'Berkshire Hathaway Inc.' },
    { symbol: 'V', name: 'Visa Inc.' },
    { symbol: 'JPM', name: 'JPMorgan Chase & Co.' },
    { symbol: 'WMT', name: 'Walmart Inc.' },
    { symbol: 'JNJ', name: 'Johnson & Johnson' },
    { symbol: 'MA', name: 'Mastercard Inc.' },
    { symbol: 'PG', name: 'Procter & Gamble Co.' },
    { symbol: 'UNH', name: 'UnitedHealth Group Inc.' },
    { symbol: 'HD', name: 'Home Depot Inc.' },
    { symbol: 'DIS', name: 'Walt Disney Co.' },
    { symbol: 'BAC', name: 'Bank of America Corp.' },
    { symbol: 'ADBE', name: 'Adobe Inc.' },
    { symbol: 'CRM', name: 'Salesforce Inc.' },
    { symbol: 'NFLX', name: 'Netflix Inc.' },
    { symbol: 'CMCSA', name: 'Comcast Corporation' },
    { symbol: 'XOM', name: 'Exxon Mobil Corporation' },
    { symbol: 'COST', name: 'Costco Wholesale Corp.' },
    { symbol: 'PEP', name: 'PepsiCo Inc.' },
    { symbol: 'TMO', name: 'Thermo Fisher Scientific Inc.' },
    { symbol: 'ABT', name: 'Abbott Laboratories' },
    { symbol: 'NKE', name: 'Nike Inc.' },
    { symbol: 'CSCO', name: 'Cisco Systems Inc.' },
    { symbol: 'ORCL', name: 'Oracle Corporation' },
    { symbol: 'AMD', name: 'Advanced Micro Devices Inc.' },
    { symbol: 'INTC', name: 'Intel Corporation' },
    { symbol: 'QCOM', name: 'Qualcomm Inc.' },
    { symbol: 'TXN', name: 'Texas Instruments Inc.' },
    { symbol: 'AVGO', name: 'Broadcom Inc.' },
    { symbol: 'CVX', name: 'Chevron Corporation' },
    { symbol: 'KO', name: 'Coca-Cola Co.' },
    { symbol: 'MCD', name: 'McDonald\'s Corporation' },
    { symbol: 'PYPL', name: 'PayPal Holdings Inc.' },
    { symbol: 'UBER', name: 'Uber Technologies Inc.' },
    { symbol: 'BA', name: 'Boeing Co.' },
    { symbol: 'CAT', name: 'Caterpillar Inc.' },
    { symbol: 'GE', name: 'General Electric Co.' },
    { symbol: 'F', name: 'Ford Motor Co.' },
    { symbol: 'GM', name: 'General Motors Co.' },
    { symbol: 'SBUX', name: 'Starbucks Corporation' },
    { symbol: 'PLTR', name: 'Palantir Technologies Inc.' },
    { symbol: 'COIN', name: 'Coinbase Global Inc.' },
    { symbol: 'SQ', name: 'Block Inc.' },
    { symbol: 'SNOW', name: 'Snowflake Inc.' },
    { symbol: 'SPY', name: 'SPDR S&P 500 ETF Trust' },
    { symbol: 'QQQ', name: 'Invesco QQQ Trust' },
    { symbol: 'IWM', name: 'iShares Russell 2000 ETF' },
    { symbol: 'DIA', name: 'SPDR Dow Jones Industrial Average ETF' },
    { symbol: 'VTI', name: 'Vanguard Total Stock Market ETF' },
    { symbol: 'VOO', name: 'Vanguard S&P 500 ETF' },
    { symbol: 'GLD', name: 'SPDR Gold Trust' },
    { symbol: 'SLV', name: 'iShares Silver Trust' },
    { symbol: 'TLT', name: 'iShares 20+ Year Treasury Bond ETF' },
    { symbol: 'XLE', name: 'Energy Select Sector SPDR Fund' },
    { symbol: 'XLF', name: 'Financial Select Sector SPDR Fund' },
    { symbol: 'XLK', name: 'Technology Select Sector SPDR Fund' },
  ];

  const handleTickerChange = (value) => {
    const upper = value.toUpperCase();
    setTicker(upper);

    if (upper.length > 0) {
      const matches = popularStocks.filter(stock =>
        stock.symbol.startsWith(upper) ||
        stock.name.toUpperCase().includes(upper)
      ).slice(0, 10);
      setFilteredTickers(matches);
      setShowTickerDropdown(matches.length > 0);
    } else {
      setFilteredTickers([]);
      setShowTickerDropdown(false);
    }
  };

  const runFFScanner = async () => {
    if (!apiKey || apiKey.trim() === '') {
      setLoadError('Please enter your Polygon.io API key to use the scanner');
      return;
    }

    setScanning(true);
    setScanResults([]);
    setScanLogs([]);
    setLoadError(null);

    const stocksToScan = [
      'SPY', 'QQQ', 'IWM', 'AAPL', 'MSFT', 'GOOGL', 'AMZN', 'NVDA',
      'META', 'TSLA', 'AMD', 'NFLX', 'COIN', 'PLTR', 'GLD', 'TLT'
    ];

    const results = [];
    const logs = [];
    const today = new Date();

    const allFridays = [];
    let currentDate = new Date(today);
    while (allFridays.length < 8) {
      if (currentDate.getDay() === 5 && currentDate > today) {
        allFridays.push(new Date(currentDate));
      }
      currentDate.setDate(currentDate.getDate() + 1);
    }

    console.log('Scanner: Using real IV data for all expiration combinations');

    const now = new Date();
    const dayOfWeek = now.getUTCDay();
    const isWeekend = dayOfWeek === 0 || dayOfWeek === 6;

    logs.push(`üîç Scanning ${stocksToScan.length} stocks for FF ‚â• ${minFF}%`);
    logs.push(`üìÖ Testing all expiration combinations (next ${allFridays.length} weeks)`);
    logs.push(isWeekend
      ? '‚è∞ Market closed (Weekend). Using last available IV data from Friday.'
      : '‚è∞ Using most recent IV data (15-min delayed)'
    );

    setScanLogs([...logs]);
    setScanProgress({ current: 0, total: stocksToScan.length });

    for (let i = 0; i < stocksToScan.length; i++) {
      const symbol = stocksToScan[i];
      setScanProgress({ current: i + 1, total: stocksToScan.length });

      try {
        const quoteUrl = `https://api.polygon.io/v2/aggs/ticker/${symbol}/prev?adjusted=true&apiKey=${apiKey}`;
        const quoteResponse = await fetch(quoteUrl);
        if (!quoteResponse.ok) continue;

        const quoteData = await quoteResponse.json();
        if (!quoteData.results || quoteData.results.length === 0) continue;

        const currentPrice = quoteData.results[0].c;
        const strike = Math.round(currentPrice / 5) * 5;

        const optionsUrl = `https://api.polygon.io/v3/snapshot/options/${symbol}?apiKey=${apiKey}`;
        const optionsResponse = await fetch(optionsUrl);
        if (!optionsResponse.ok) {
          console.error(`${symbol}: Failed to fetch options data`);
          continue;
        }

        const optionsData = await optionsResponse.json();
        if (!optionsData.results || optionsData.results.length === 0) {
          logs.push(`‚ùå ${symbol}: No options data from API`);
          setScanLogs([...logs]);
          continue;
        }

        const callOptions = optionsData.results.filter(opt =>
          opt.details &&
          opt.details.contract_type === 'call' &&
          opt.implied_volatility &&
          opt.implied_volatility > 0 &&
          Math.abs(opt.details.strike_price - strike) < 20
        );

        if (callOptions.length === 0) {
          logs.push(`‚ùå ${symbol}: No call options with IV data`);
          setScanLogs([...logs]);
          continue;
        }

        const optionsByExpiration = {};
        callOptions.forEach(opt => {
          const expDate = opt.details.expiration_date;
          if (!optionsByExpiration[expDate]) {
            optionsByExpiration[expDate] = [];
          }
          optionsByExpiration[expDate].push(opt);
        });

        const expirationDates = Object.keys(optionsByExpiration).sort();
        if (expirationDates.length < 2) {
          logs.push(`‚ùå ${symbol}: Need at least 2 expirations (found ${expirationDates.length})`);
          setScanLogs([...logs]);
          continue;
        }

        let bestFF = -Infinity;
        let bestCombo = null;

        for (let a = 0; a < expirationDates.length - 1; a++) {
          for (let b = a + 1; b < expirationDates.length; b++) {
            const exp1 = expirationDates[a];
            const exp2 = expirationDates[b];

            const exp1Opts = optionsByExpiration[exp1].sort((x, y) =>
              Math.abs(x.details.strike_price - strike) - Math.abs(y.details.strike_price - strike)
            );
            const exp2Opts = optionsByExpiration[exp2].sort((x, y) =>
              Math.abs(x.details.strike_price - strike) - Math.abs(y.details.strike_price - strike)
            );

            const iv1Data = exp1Opts[0].implied_volatility;
            const iv2Data = exp2Opts[0].implied_volatility;

            if (!iv1Data || !iv2Data) continue;

            const iv1 = iv1Data;
            const iv2 = iv2Data;

            const exp1DateObj = new Date(exp1);
            const exp2DateObj = new Date(exp2);
            const msPerDay = 24 * 60 * 60 * 1000;
            const daysToExp1 = (exp1DateObj - today) / msPerDay;
            const daysToExp2 = (exp2DateObj - today) / msPerDay;

            if (daysToExp1 <= 0 || daysToExp2 <= 0 || daysToExp2 <= daysToExp1) continue;

            const T1 = daysToExp1 / 365;
            const T2 = daysToExp2 / 365;
            const v1 = iv1;
            const v2 = iv2;

            const numerator = (T2 * v2 * v2) - (T1 * v1 * v1);
            const denominator = T2 - T1;

            if (numerator < 0 || denominator <= 0) continue;

            const forwardVol = Math.sqrt(numerator / denominator);
            const forwardFactor = ((v1 / forwardVol) - 1) * 100;

            if (forwardFactor > bestFF) {
              bestFF = forwardFactor;
              bestCombo = {
                exp1,
                exp2,
                iv1: v1 * 100,
                iv2: v2 * 100,
                dte1: daysToExp1,
                dte2: daysToExp2,
                fv: forwardVol * 100,
                ff: forwardFactor,
                strike: exp1Opts[0].details.strike_price
              };
            }
          }
        }

        if (!bestCombo) {
          logs.push(`‚ùå ${symbol}: No valid combinations found`);
          setScanLogs([...logs]);
          continue;
        }

        console.log(`${symbol}: Best FF=${bestCombo.ff.toFixed(2)}% (${bestCombo.exp1} ‚Üí ${bestCombo.exp2})`);

        if (bestCombo.ff >= minFF) {
          logs.push(`‚úÖ ${symbol}: FF=${bestCombo.ff.toFixed(2)}% (${bestCombo.exp1}‚Üí${bestCombo.exp2})`);
          setScanLogs([...logs]);

          results.push({
            symbol,
            price: currentPrice.toFixed(2),
            strike: bestCombo.strike.toFixed(0),
            ff: bestCombo.ff.toFixed(2),
            fv: bestCombo.fv.toFixed(2),
            iv1: bestCombo.iv1.toFixed(1),
            iv2: bestCombo.iv2.toFixed(1),
            dte1: bestCombo.dte1.toFixed(1),
            dte2: bestCombo.dte2.toFixed(1),
            exp1: bestCombo.exp1,
            exp2: bestCombo.exp2
          });
        } else {
          logs.push(`‚ö™ ${symbol}: Best FF=${bestCombo.ff.toFixed(2)}% (below threshold)`);
          setScanLogs([...logs]);
        }

        await new Promise(resolve => setTimeout(resolve, 100));
      } catch (error) {
        console.error(`Error scanning ${symbol}:`, error);
      }
    }

    results.sort((a, b) => parseFloat(b.ff) - parseFloat(a.ff));

    const finalMsg = `üéØ Scan complete: Found ${results.length} stocks with FF ‚â• ${minFF}%`;
    console.log(finalMsg);
    logs.push(finalMsg);
    setScanLogs([...logs]);

    setScanResults(results);
    setScanning(false);
    setScanProgress({ current: 0, total: 0 });

    if (results.length === 0) {
      setLoadError(`No stocks found with FF ‚â• ${minFF}%. Try lowering the threshold to 20% or 15%.`);
    }
  };

  const selectTicker = (symbol) => {
    setTicker(symbol);
    setShowTickerDropdown(false);
    setFilteredTickers([]);
  };

  const loadDemoData = () => {
    // Demo data for SPY
    setTicker('SPY');
    setSpotPrice('580.25');
    setStrikePrice('580');
    setDate1('2025-10-24');
    setDate2('2025-10-31');
    setIv1('12.5');
    setIv2('11.8');
    setRiskFreeRate('4.5');
    setDividend('1.2');
    setVolumeCallT1(45230);
    setVolumeCallT2(12450);
    setVolumePutT1(38920);
    setVolumePutT2(9870);
    setOiCallT1(128500);
    setOiCallT2(67300);
    setOiPutT1(145600);
    setOiPutT2(52100);
    setDataSource('demo');

    // Auto-calculate
    setTimeout(() => calculateResults(), 100);
  };

  const fetchOptionData = async (tickerSymbol) => {
    if (!tickerSymbol || tickerSymbol.trim() === '') {
      setLoadError('Please enter a ticker symbol');
      return;
    }

    if (!apiKey || apiKey.trim() === '') {
      setLoadError('Please enter your Polygon.io API key');
      return;
    }

    setLoading(true);
    setLoadError(null);
    setVolumeCallT1(null);
    setVolumeCallT2(null);
    setVolumePutT1(null);
    setVolumePutT2(null);
    setOiCallT1(null);
    setOiCallT2(null);
    setOiPutT1(null);
    setOiPutT2(null);
    setDataSource(null);

    try {
      const symbol = tickerSymbol.toUpperCase().trim();
      console.log('Fetching data for:', symbol);

      localStorage.setItem('polygonKey', apiKey);

      const today = new Date();
      const quoteUrl = `https://api.polygon.io/v2/aggs/ticker/${symbol}/prev?adjusted=true&apiKey=${apiKey}`;
      console.log('Fetching quote from Polygon.io...');

      const quoteResponse = await fetch(quoteUrl);
      if (!quoteResponse.ok) {
        throw new Error(`API Error (${quoteResponse.status}). Check your API key.`);
      }

      const quoteData = await quoteResponse.json();
      console.log('Quote data:', quoteData);

      if (quoteData.status === 'ERROR') {
        throw new Error(quoteData.error || 'Invalid ticker symbol');
      }

      if (!quoteData.results || quoteData.results.length === 0) {
        throw new Error('No price data available for this ticker');
      }

      const result = quoteData.results[0];
      const currentPrice = result.c;

      if (!currentPrice || currentPrice <= 0) {
        throw new Error('No valid price data available');
      }

      setSpotPrice(currentPrice.toFixed(2));

      const fridays = [];
      let currentDate = new Date(today);

      while (fridays.length < 2) {
        if (currentDate.getDay() === 5 && currentDate > today) {
          fridays.push(new Date(currentDate));
        }
        currentDate.setDate(currentDate.getDate() + 1);
      }

      setDate1(fridays[0].toISOString().split('T')[0]);
      setDate2(fridays[1].toISOString().split('T')[0]);

      const roundedStrike = Math.round(currentPrice / 5) * 5;
      setStrikePrice(roundedStrike.toFixed(2));

      try {
        const exp1Date = fridays[0].toISOString().split('T')[0];
        const exp2Date = fridays[1].toISOString().split('T')[0];

        console.log('üìä Fetching options data for expirations:', exp1Date, exp2Date);
        console.log('üí° Using Option Chain Snapshot API with market data');

        // Track if we got real IV data
        let gotRealIV1 = false;
        let gotRealIV2 = false;

        // ‚úÖ USE OPTION CHAIN SNAPSHOT API - This returns IV, Volume, OI directly!
        // API: /v3/snapshot/options/{underlying_ticker}
        // Filter by expiration_date and contract_type to get ATM options

        // Fetch T1 options using Option Chain Snapshot API
        const chain1Url = `https://api.polygon.io/v3/snapshot/options/${symbol}?expiration_date.gte=${exp1Date}&expiration_date.lte=${exp1Date}&contract_type=call&limit=250&apiKey=${apiKey}`;
        console.log('üîç Fetching T1 chain snapshot:', chain1Url);

        const chain1Response = await fetch(chain1Url);
        if (chain1Response.ok) {
          const chain1Data = await chain1Response.json();
          console.log('üì¶ T1 Chain Snapshot Response:', chain1Data);

          if (chain1Data.status === 'OK' && chain1Data.results && chain1Data.results.length > 0) {
            // Find ATM call option (closest strike to current price)
            const atmCall = chain1Data.results
              .filter(opt => opt.details?.strike_price && Math.abs(opt.details.strike_price - roundedStrike) < 20)
              .sort((a, b) => Math.abs(a.details.strike_price - roundedStrike) - Math.abs(b.details.strike_price - roundedStrike))[0];

            if (atmCall) {
              console.log('‚úÖ Found T1 ATM Call:', atmCall.details?.ticker, 'Strike:', atmCall.details?.strike_price);

              // Extract IV from snapshot data
              if (atmCall.implied_volatility !== undefined && atmCall.implied_volatility > 0) {
                const iv1Value = (atmCall.implied_volatility * 100).toFixed(2);
                setIv1(iv1Value);
                gotRealIV1 = true;
                console.log(`‚úÖ T1 IV: ${iv1Value}%`);
              } else {
                console.log('‚ö†Ô∏è T1 IV not available:', atmCall.implied_volatility);
              }

              // Extract Volume
              if (atmCall.day?.volume !== undefined) {
                setVolumeCallT1(atmCall.day.volume);
                console.log(`‚úÖ T1 Call Volume: ${atmCall.day.volume}`);
              }

              // Extract Open Interest
              if (atmCall.open_interest !== undefined) {
                setOiCallT1(atmCall.open_interest);
                console.log(`‚úÖ T1 Call OI: ${atmCall.open_interest}`);
              }
            } else {
              console.log('‚ö†Ô∏è No ATM call found for T1 (strike ~', roundedStrike, ')');
            }
          } else {
            console.log('‚ö†Ô∏è No results in T1 chain snapshot');
          }
        } else {
          const errorText = await chain1Response.text();
          console.error('‚ùå T1 chain snapshot failed:', chain1Response.status, errorText);
        }

        // Fetch T2 options using Option Chain Snapshot API
        const chain2Url = `https://api.polygon.io/v3/snapshot/options/${symbol}?expiration_date.gte=${exp2Date}&expiration_date.lte=${exp2Date}&contract_type=call&limit=250&apiKey=${apiKey}`;
        console.log('üîç Fetching T2 chain snapshot:', chain2Url);

        const chain2Response = await fetch(chain2Url);
        if (chain2Response.ok) {
          const chain2Data = await chain2Response.json();
          console.log('üì¶ T2 Chain Snapshot Response:', chain2Data);

          if (chain2Data.status === 'OK' && chain2Data.results && chain2Data.results.length > 0) {
            // Find ATM call option
            const atmCall2 = chain2Data.results
              .filter(opt => opt.details?.strike_price && Math.abs(opt.details.strike_price - roundedStrike) < 20)
              .sort((a, b) => Math.abs(a.details.strike_price - roundedStrike) - Math.abs(b.details.strike_price - roundedStrike))[0];

            if (atmCall2) {
              console.log('‚úÖ Found T2 ATM Call:', atmCall2.details?.ticker, 'Strike:', atmCall2.details?.strike_price);

              // Extract IV
              if (atmCall2.implied_volatility !== undefined && atmCall2.implied_volatility > 0) {
                const iv2Value = (atmCall2.implied_volatility * 100).toFixed(2);
                setIv2(iv2Value);
                gotRealIV2 = true;
                console.log(`‚úÖ T2 IV: ${iv2Value}%`);
              } else {
                console.log('‚ö†Ô∏è T2 IV not available:', atmCall2.implied_volatility);
              }

              // Extract Volume
              if (atmCall2.day?.volume !== undefined) {
                setVolumeCallT2(atmCall2.day.volume);
                console.log(`‚úÖ T2 Call Volume: ${atmCall2.day.volume}`);
              }

              // Extract Open Interest
              if (atmCall2.open_interest !== undefined) {
                setOiCallT2(atmCall2.open_interest);
                console.log(`‚úÖ T2 Call OI: ${atmCall2.open_interest}`);
              }
            } else {
              console.log('‚ö†Ô∏è No ATM call found for T2 (strike ~', roundedStrike, ')');
            }

            // Now get PUT options for T2 from same chain
            const atmPut2 = chain2Data.results
              .filter(opt => opt.details?.contract_type === 'put' && opt.details?.strike_price && Math.abs(opt.details.strike_price - roundedStrike) < 20)
              .sort((a, b) => Math.abs(a.details.strike_price - roundedStrike) - Math.abs(b.details.strike_price - roundedStrike))[0];

            if (atmPut2) {
              if (atmPut2.day?.volume !== undefined) {
                setVolumePutT2(atmPut2.day.volume);
                console.log(`‚úÖ T2 Put Volume: ${atmPut2.day.volume}`);
              }
              if (atmPut2.open_interest !== undefined) {
                setOiPutT2(atmPut2.open_interest);
                console.log(`‚úÖ T2 Put OI: ${atmPut2.open_interest}`);
              }
            }
          } else {
            console.log('‚ö†Ô∏è No results in T2 chain snapshot');
          }
        } else {
          const errorText = await chain2Response.text();
          console.error('‚ùå T2 chain snapshot failed:', chain2Response.status, errorText);
        }

        // Fetch PUT options for T1 using same approach
        const chain1PutUrl = `https://api.polygon.io/v3/snapshot/options/${symbol}?expiration_date.gte=${exp1Date}&expiration_date.lte=${exp1Date}&contract_type=put&limit=250&apiKey=${apiKey}`;
        console.log('üîç Fetching T1 put chain snapshot');

        const chain1PutResponse = await fetch(chain1PutUrl);
        if (chain1PutResponse.ok) {
          const chain1PutData = await chain1PutResponse.json();
          if (chain1PutData.status === 'OK' && chain1PutData.results && chain1PutData.results.length > 0) {
            const atmPut1 = chain1PutData.results
              .filter(opt => opt.details?.strike_price && Math.abs(opt.details.strike_price - roundedStrike) < 20)
              .sort((a, b) => Math.abs(a.details.strike_price - roundedStrike) - Math.abs(b.details.strike_price - roundedStrike))[0];

            if (atmPut1) {
              if (atmPut1.day?.volume !== undefined) {
                setVolumePutT1(atmPut1.day.volume);
                console.log(`‚úÖ T1 Put Volume: ${atmPut1.day.volume}`);
              }
              if (atmPut1.open_interest !== undefined) {
                setOiPutT1(atmPut1.open_interest);
                console.log(`‚úÖ T1 Put OI: ${atmPut1.open_interest}`);
              }
            }
          }
        }

        // Set data source based on whether we got real IV
        if (gotRealIV1 && gotRealIV2) {
          setDataSource('real');
          console.log('‚úÖ Real options data loaded with IV from Polygon.io!');
        } else if (gotRealIV1 || gotRealIV2) {
          setDataSource('partial');
          console.log('‚ö†Ô∏è Partial options data - some IV missing');
        } else {
          setDataSource('estimated');
          console.log('‚ö†Ô∏è No IV data available - using estimates');
        }
      } catch (optError) {
        console.error('Options data error:', optError);
        setIv1('30');
        setIv2('28');
        setDataSource('estimated');
      }

      setLoading(false);
      setLoadError(null);
      setTimeout(() => calculateResults(), 100);
    } catch (error) {
      console.error('Error fetching option data:', error);
      setLoadError(error.message || 'Failed to load option data');
      setLoading(false);
    }
  };

  const getNextFridays = () => {
    const today = new Date();
    let fridays = [];
    let currentDate = new Date(today);

    while (fridays.length < 50) {
      if (currentDate.getDay() === 5) {
        fridays.push(new Date(currentDate));
      }
      currentDate.setDate(currentDate.getDate() + 1);
    }
    return fridays;
  };

  const normDist = (x) => {
    const a1 = 0.254829592;
    const a2 = -0.284496736;
    const a3 = 1.421413741;
    const a4 = -1.453152027;
    const a5 = 1.061405429;
    const p = 0.3275911;

    const sign = x < 0 ? -1 : 1;
    x = Math.abs(x) / Math.sqrt(2);

    const t = 1.0 / (1.0 + p * x);
    const y = 1.0 - (((((a5 * t + a4) * t + a3) * t + a2) * t + a1) * t * Math.exp(-x * x));

    return 0.5 * (1.0 + sign * y);
  };

  const blackScholesCall = (S, K, T, r, sigma, q = 0) => {
    if (T <= 0 || sigma <= 0) return Math.max(S - K, 0);

    const sqrtT = Math.sqrt(T);
    const d1 = (Math.log(S / K) + (r - q + 0.5 * sigma * sigma) * T) / (sigma * sqrtT);
    const d2 = d1 - sigma * sqrtT;

    const callPrice = S * Math.exp(-q * T) * normDist(d1) - K * Math.exp(-r * T) * normDist(d2);
    return Math.max(callPrice, 0);
  };

  const blackScholesPut = (S, K, T, r, sigma, q = 0) => {
    if (T <= 0 || sigma <= 0) return Math.max(K - S, 0);

    const sqrtT = Math.sqrt(T);
    const d1 = (Math.log(S / K) + (r - q + 0.5 * sigma * sigma) * T) / (sigma * sqrtT);
    const d2 = d1 - sigma * sqrtT;

    const putPrice = K * Math.exp(-r * T) * normDist(-d2) - S * Math.exp(-q * T) * normDist(-d1);
    return Math.max(putPrice, 0);
  };

  const black76Call = (F, K, T, r, sigma) => {
    if (T <= 0 || sigma <= 0) return Math.max(F - K, 0);

    const sqrtT = Math.sqrt(T);
    const d1 = (Math.log(F / K) + (0.5 * sigma * sigma) * T) / (sigma * sqrtT);
    const d2 = d1 - sigma * sqrtT;

    const callPrice = Math.exp(-r * T) * (F * normDist(d1) - K * normDist(d2));
    return Math.max(callPrice, 0);
  };

  const black76Put = (F, K, T, r, sigma) => {
    if (T <= 0 || sigma <= 0) return Math.max(K - F, 0);

    const sqrtT = Math.sqrt(T);
    const d1 = (Math.log(F / K) + (0.5 * sigma * sigma) * T) / (sigma * sqrtT);
    const d2 = d1 - sigma * sqrtT;

    const putPrice = Math.exp(-r * T) * (K * normDist(-d2) - F * normDist(-d1));
    return Math.max(putPrice, 0);
  };

  const binomialCall = (S, K, T, r, sigma, q = 0, steps = 50) => {
    const dt = T / steps;
    const u = Math.exp(sigma * Math.sqrt(dt));
    const d = 1 / u;
    const p = (Math.exp((r - q) * dt) - d) / (u - d);

    // Stock prices at maturity
    const prices = [];
    for (let i = 0; i <= steps; i++) {
      prices[i] = S * Math.pow(u, steps - i) * Math.pow(d, i);
    }

    // Option values at maturity
    const values = prices.map(price => Math.max(price - K, 0));

    // Backward induction
    for (let j = steps - 1; j >= 0; j--) {
      for (let i = 0; i <= j; i++) {
        values[i] = Math.exp(-r * dt) * (p * values[i] + (1 - p) * values[i + 1]);
      }
    }

    return values[0];
  };

  const binomialPut = (S, K, T, r, sigma, q = 0, steps = 50) => {
    const dt = T / steps;
    const u = Math.exp(sigma * Math.sqrt(dt));
    const d = 1 / u;
    const p = (Math.exp((r - q) * dt) - d) / (u - d);

    // Stock prices at maturity
    const prices = [];
    for (let i = 0; i <= steps; i++) {
      prices[i] = S * Math.pow(u, steps - i) * Math.pow(d, i);
    }

    // Option values at maturity
    const values = prices.map(price => Math.max(K - price, 0));

    // Backward induction
    for (let j = steps - 1; j >= 0; j--) {
      for (let i = 0; i <= j; i++) {
        values[i] = Math.exp(-r * dt) * (p * values[i] + (1 - p) * values[i + 1]);
      }
    }

    return values[0];
  };

  const getOptionPrice = (S, K, T, r, sigma, q, isCall) => {
    switch(pricingModel) {
      case 'black76':
        const F = S * Math.exp((r - q) * T);
        return isCall ? black76Call(F, K, T, r, sigma) : black76Put(F, K, T, r, sigma);
      case 'binomial':
        return isCall ? binomialCall(S, K, T, r, sigma, q) : binomialPut(S, K, T, r, sigma, q);
      case 'blackscholes':
      default:
        return isCall ? blackScholesCall(S, K, T, r, sigma, q) : blackScholesPut(S, K, T, r, sigma, q);
    }
  };

  const handlePriceSlider = (newPrice, isCallSpread) => {
    if (!result) return;

    const S = parseFloat(spotPrice);
    const K = parseFloat(strikePrice);
    const v1 = parseFloat(iv1) / 100;
    const r = parseFloat(riskFreeRate) / 100;
    const q = parseFloat(dividend) / 100;

    const d1 = new Date(date1);
    const d2 = new Date(date2);
    const now = new Date();

    const d1_16NY = new Date(d1);
    d1_16NY.setHours(22, 0, 0, 0);

    const d2_16NY = new Date(d2);
    d2_16NY.setHours(22, 0, 0, 0);

    const msPerDay = 24 * 60 * 60 * 1000;
    const daysToExp1 = (d1_16NY - now) / msPerDay;
    const daysToExp2 = (d2_16NY - now) / msPerDay;

    const T1 = daysToExp1 / 365;
    const T2 = daysToExp2 / 365;

    const frontPrice = isCallSpread 
      ? getOptionPrice(S, K, T1, r, v1, q, true)
      : getOptionPrice(S, K, T1, r, v1, q, false);

    // Target Back-Option Preis basierend auf neuer Spread-Preis
    const targetBackPrice = newPrice + frontPrice;

    // Bin√§re Suche nach der IV2, die zu targetBackPrice f√ºhrt
    let v2Low = 0.001;
    let v2High = 3.0;
    let v2Mid = 1.0;

    for (let iteration = 0; iteration < 100; iteration++) {
      v2Mid = (v2Low + v2High) / 2;
      const backPrice = isCallSpread 
        ? getOptionPrice(S, K, T2, r, v2Mid, q, true)
        : getOptionPrice(S, K, T2, r, v2Mid, q, false);

      const diff = backPrice - targetBackPrice;

      if (Math.abs(diff) < 0.00001) break;

      if (diff > 0) {
        v2High = v2Mid;
      } else {
        v2Low = v2Mid;
      }
    }

    // Mit dieser gefundenen IV2 berechne den echten FF
    const numerator = (T2 * v2Mid * v2Mid) - (T1 * v1 * v1);
    const denominator = T2 - T1;

    let newFF;
    if (numerator >= 0 && denominator > 0) {
      const forwardVol = Math.sqrt(numerator / denominator);
      newFF = ((v1 / forwardVol) - 1) * 100;
    } else {
      newFF = parseFloat(result.forwardFactor);
    }

    if (isCallSpread) {
      setCallPriceSlider(newPrice);
      setAdjustedFFCall(newFF);
    } else {
      setPutPriceSlider(newPrice);
      setAdjustedFFPut(newFF);
    }
  };

  const calculateResults = () => {
    try {
      const d1 = new Date(date1);
      const d2 = new Date(date2);

      if (d2 <= d1) {
        setResult({ error: 'Expiration 2 muss nach Expiration 1 liegen' });
        return;
      }

      // Berechne verbleibende Zeit mit Ber√ºcksichtigung der NY-Schlie√üzeit (16:00 EST = 22:00 Berlin)
      const now = new Date();
      const d1_16NY = new Date(d1);
      d1_16NY.setHours(22, 0, 0, 0); // 16:00 EST = 22:00 Berlin (CET)

      const d2_16NY = new Date(d2);
      d2_16NY.setHours(22, 0, 0, 0); // 16:00 EST = 22:00 Berlin (CET)

      // Berechne verbleibende Tage mit Dezimalgenauigkeit (keine Rundung)
      const msPerDay = 24 * 60 * 60 * 1000;
      const daysToExp1 = (d1_16NY - now) / msPerDay;
      const daysToExp2 = (d2_16NY - now) / msPerDay;

      if (daysToExp1 <= 0 || daysToExp2 <= 0) {
        setResult({ error: 'Ablaufdatum liegt in der Vergangenheit' });
        return;
      }

      // Zeitberechnung in Jahren - KEINE Rundung!
      const T1 = daysToExp1 / 365;
      const T2 = daysToExp2 / 365;

      const v1 = parseFloat(iv1) / 100;
      const v2 = parseFloat(iv2) / 100;
      const S = parseFloat(spotPrice);
      const K = parseFloat(strikePrice);
      const r = parseFloat(riskFreeRate) / 100;
      const q = parseFloat(dividend) / 100;

      if (v1 <= 0 || v2 <= 0 || S <= 0 || K <= 0) {
        setResult({ error: 'Bitte √ºberpr√ºfe deine Eingaben' });
        return;
      }

      const numerator = (T2 * v2 * v2) - (T1 * v1 * v1);
      const denominator = T2 - T1;

      if (numerator < 0) {
        setResult({ error: 'Ung√ºltige IV Kombination - Forward Vol w√§re imagin√§r' });
        return;
      }

      const forwardVol = Math.sqrt(numerator / denominator);
      const forwardVolPct = forwardVol * 100;
      const forwardFactor = (v1 / forwardVol) - 1;

      const callT1 = getOptionPrice(S, K, T1, r, v1, q, true);
      const callT2 = getOptionPrice(S, K, T2, r, v2, q, true);
      const callCalendarSpread = callT2 - callT1;

      const putT1 = getOptionPrice(S, K, T1, r, v1, q, false);
      const putT2 = getOptionPrice(S, K, T2, r, v2, q, false);
      const putCalendarSpread = putT2 - putT1;

      // FF = 30%
      const targetForwardFactor30 = 0.30;
      const requiredForwardVol30 = v1 / (1 + targetForwardFactor30);
      const iv2Squared30 = (requiredForwardVol30 * requiredForwardVol30 * (T2 - T1) + T1 * v1 * v1) / T2;
      const requiredIV230 = Math.sqrt(Math.max(0, iv2Squared30));

      const callT2At30 = getOptionPrice(S, K, T2, r, requiredIV230, q, true);
      const maxCallCalendarPrice30 = callT2At30 - callT1;

      const putT2At30 = getOptionPrice(S, K, T2, r, requiredIV230, q, false);
      const maxPutCalendarPrice30 = putT2At30 - putT1;

      // FF = 0%
      const targetForwardFactor0 = 0;
      const requiredForwardVol0 = v1 / (1 + targetForwardFactor0);
      const iv2Squared0 = (requiredForwardVol0 * requiredForwardVol0 * (T2 - T1) + T1 * v1 * v1) / T2;
      const requiredIV20 = Math.sqrt(Math.max(0, iv2Squared0));

      const callT2At0 = getOptionPrice(S, K, T2, r, requiredIV20, q, true);
      const callCalendarSpreadAt0 = callT2At0 - callT1;

      const putT2At0 = getOptionPrice(S, K, T2, r, requiredIV20, q, false);
      const putCalendarSpreadAt0 = putT2At0 - putT1;

      const newResult = {
        forwardVolPct: parseFloat(forwardVolPct).toFixed(2),
        forwardFactor: (parseFloat(forwardFactor) * 100).toFixed(2),
        daysToExp1: parseFloat(daysToExp1).toFixed(2),
        daysToExp2: parseFloat(daysToExp2).toFixed(2),
        callT1: parseFloat(callT1).toFixed(2),
        callT2: parseFloat(callT2).toFixed(2),
        callCalendarSpread: parseFloat(callCalendarSpread).toFixed(2),
        putT1: parseFloat(putT1).toFixed(2),
        putT2: parseFloat(putT2).toFixed(2),
        putCalendarSpread: parseFloat(putCalendarSpread).toFixed(2),
        maxCallCalendarPrice30: Math.max(0, parseFloat(maxCallCalendarPrice30)).toFixed(2),
        maxPutCalendarPrice30: Math.max(0, parseFloat(maxPutCalendarPrice30)).toFixed(2),
        callCalendarSpreadAt0: parseFloat(callCalendarSpreadAt0).toFixed(2),
        putCalendarSpreadAt0: parseFloat(putCalendarSpreadAt0).toFixed(2),
        error: null,
        timestamp: new Date().toLocaleString('de-DE')
      };

      setResult(newResult);

      const historyEntry = {
        ticker: ticker || 'N/A',
        date: date1,
        ff: parseFloat(newResult.forwardFactor),
        callSpreadCurrent: newResult.callCalendarSpread,
        putSpreadCurrent: newResult.putCalendarSpread,
        callSpreadFF30: newResult.maxCallCalendarPrice30,
        putSpreadFF30: newResult.maxPutCalendarPrice30,
        callSpreadFF0: newResult.callCalendarSpreadAt0,
        putSpreadFF0: newResult.putCalendarSpreadAt0,
        timestamp: newResult.timestamp
      };
      setTradeHistory([historyEntry, ...tradeHistory.slice(0, 9)]);

    } catch (err) {
      setResult({ error: 'Fehler bei der Berechnung: ' + err.message });
    }
  };

  const renderCalendar = (value, onChange, show, setShow) => {
    if (!show) return null;

    const today = new Date();
